CREATE TABLE "PREDICTOR_FACTORY"."propagated_loan_001" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."base_sampled" t1 INNER JOIN "FINANCIAL"."loan" t2 ON t1."propagated_id1" = t2."loan_id";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_account_002" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_loan_001" t1 INNER JOIN "FINANCIAL"."account" t2 ON t1."account_id" = t2."account_id";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_district_003" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_account_002" t1 INNER JOIN "FINANCIAL"."district" t2 ON t1."district_id" = t2."district_id";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_disp_004" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_account_002" t1 INNER JOIN "FINANCIAL"."disp" t2 ON t1."account_id" = t2."account_id";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_order_005" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_account_002" t1 INNER JOIN "FINANCIAL"."order" t2 ON t1."account_id" = t2."account_id";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_trans_006" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_account_002" t1 INNER JOIN "FINANCIAL"."trans" t2 ON t1."account_id" = t2."account_id" WHERE t2."date" <= add_months("propagated_date", -1) AND add_months("propagated_date", -13) <= t2."date";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_client_007" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_district_003" t1 INNER JOIN "FINANCIAL"."client" t2 ON t1."district_id" = t2."district_id";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_client_008" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_disp_004" t1 INNER JOIN "FINANCIAL"."client" t2 ON t1."client_id" = t2."client_id";
CREATE TABLE "PREDICTOR_FACTORY"."propagated_card_009" AS SELECT t1."propagated_id1", t1."propagated_date", t1."propagated_target", t1."propagated_fold", t2.* FROM "PREDICTOR_FACTORY"."propagated_disp_004" t1 INNER JOIN "FINANCIAL"."card" t2 ON t1."disp_id" = t2."disp_id";
